import { Component } from '@angular/core';
import { TodoModel } from './todo.model';

@Component({
  selector: 'todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})

export class TodoComponent {
  constructor(){}
  displayAll:boolean = true;
  inputText:string = "";
  private name:string = "Osman"
  getName():string{
    return this.name;
  }
  private items:TodoModel[] =[
    {id:1,description: "Get out of bed", done: true},
    {id:2,description: "Brush teeth", done: false},
    {id:3,description: "Eat breakfast", done: false},
    {id:4,description: "Go to work", done: false},
  ]
  addItem(){
    if (this.inputText !== "") {
      this.items.push({id:this.items.length+1,description: this.inputText, done: false})

    }else{
      alert("Please enter a description")
    }
  }
  getItems():TodoModel[]{
    if (this.displayAll) {
      return this.items
      }else{
        return this.items.filter(item => !item.done)
      }
    }
}
